export const DOTS = "...";

function usePagination({
  currentPage,
  totalCount,
  pageSize
}) {

  /*
    Rewrite the logic here to map out the pagination to be displayed

    !!!!!! ATTENTION !!!!!!
    Please replace this comment here with a description of this hook.
    
  */
  const totalPage = Math.ceil(totalCount / pageSize)
  let paginationRange = []

  if (currentPage === 1 && currentPage + 2 <= totalPage - 1) {
    paginationRange = [1, 2, 3, DOTS, totalPage]
  }
  if (currentPage > 1 && currentPage < totalPage) {
    paginationRange = [1, DOTS, currentPage, DOTS, totalPage]
  }
  if (currentPage + 1 === totalPage) {
    paginationRange = [1, DOTS, currentPage - 1, currentPage, totalPage]
  }
  if (currentPage === totalPage) {
    paginationRange = [1, DOTS, currentPage - 2, currentPage - 1, currentPage]
  }
  if (totalPage === 1) {
    paginationRange = [1]
  }
  return paginationRange;
}

export default usePagination;
