    Please replace this comment here with a description of this hook.
    
  */
  const totalPage = Math.ceil(totalCount/pageSize)
  const paginationRange = []
  for(let i=1;i<=totalPage;i++){
    paginationRange.push(i)
  }
  if(currentPage === 1 && currentPage + 2 <= totalPage) {
    paginationRange[paginationRange.length-2] = DOTS
  }
  else if(currentPage - 1 > 1 && currentPage + 1 < totalPage){
    paginationRange[1] = DOTS 
    paginationRange[paginationRange.length-2] = DOTS
  }
  return paginationRange;
}

