export const DOTS = "...";


function usePagination({
  currentPage,
  totalCount,
  pageSize
}) {
  const totalPage = Math.ceil(totalCount / pageSize)
  const hasLeftSibling = (cur) =>{
    return cur - 1 > 0
  }
  const hasRightSibling = (cur) =>{
    return cur + 1 <= totalPage
  }

  let paginationRange = []
  // if(totalPage>3){
  //   if(hasLeftSibling(currentPage) && hasRightSibling(currentPage)){
  //     if(currentPage !== 1){
  //       paginationRange = [1 , currentPage , currentPage - 1, DOTS, totalPage]
  //     }

  //   }
  // }
  if(totalPage===3){
    paginationRange = [1,2,3]
  }
  else if(totalPage===2){
    paginationRange = [1,2]
  }
  else if(totalPage === 1){
    paginationRange = [1]
  }
  else if(totalPage > 3){
    if(currentPage===1){
      paginationRange = [1,2,3,DOTS,totalPage]
    }
    else if(currentPage===totalPage){
      paginationRange = [1,DOTS,totalPage-2, totalPage-1, totalPage]
    }
    else{
      if(hasLeftSibling(currentPage) && currentPage - 1 !==1){
        paginationRange = [1, DOTS, currentPage - 1, currentPage, currentPage + 1 ]
      }
    }
  }

  
  
  
  
  
  
  paginationRange = [1,DOTS,currentPage - 1, currentPage, currentPage + 1, DOTS, totalPage]
  
  return paginationRange;
}

export default usePagination;
