import React from "react";
import blogs from "../data/blogs.json";
import "../css/sidebar.scss";
import { useState } from "react";

const exampleFooter = [
  "Help",
  "Status",
  "Writers",
  "Blog",
  "Careers",
  "Privacy",
  "Terms",
  "About",
];
const tagArr = blogs.tags.map(tag=>{
    return {name:tag,activate:false}
  } 
)
function StickySidebar() {
  const [tagsState, setTagStates] = useState(tagArr)

  const onChangeTag = (tagName) =>{
    setTagStates(prevState =>{
      const newTagsState = [...prevState]
      newTagsState.map(tag=>tag.activate=false)
      const index = newTagsState.findIndex(tag=>tag.name===tagName)
      newTagsState[index].activate = true
      return newTagsState
    })
  }

  return (
    <div className="sidebar">
      <p>discover more of what matters to you</p>
      <div className="tagWrapper">
        {tagArr.map((tag) => (
          <div className={`tag ${tag.activate?'active':''}`} key={tag.name} onClick={onChangeTag.bind(tag.name)}>
            {tag.name}
          </div>
        ))}
      </div>
      <div className="tagWrapper">
        {exampleFooter.map((footer) => (
          <a href="/#" key={footer} style={{ color: "black" }}>
            {footer}
          </a>
        ))}
      </div>
    </div>
  );
}

export default StickySidebar;
